class My_View_Helper_Navigation extends Zend_View_Helper_Navigation\n{\n    public function addPage($config)\n    {\n        // Ensure 'id' is set, if not provide a default\n        foreach ($config->toArray() as $page) {\n            if (!isset($page['params']['id'])) {\n                $page['params']['id'] = 0; // default value\n            }\n        }\n        parent::addPage($config);\n    }\n}