protected function _initRouter()\n{\n    $this->bootstrap("FrontController");\n    $frontController = $this->getResource("FrontController");\n\n    // Define route with defaults to avoid missing parameters\n    $route = new Zend_Controller_Router_Route(\n        ":module/:controller/:action/:id",\n        array(\n            "module" => "default",\n            "controller" => "index",\n            "action" => "index",\n            "id" => null\n        ),\n        array(\n            "module" => "\w+",\n            "controller" => "\w+",\n            "action" => "\w+",\n            "id" => "\d*"\n        )\n    );\n    $frontController->getRouter()->addRoute('default', $route);\n}