<?php\n// Database connection\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Execute your query and store the result\n$query = "SELECT * FROM TempTable"; // Replace TempTable with your actual temp table name\n$doResults = $conn->query($query);\n\n// Check if the query was successful\nif ($doResults === false) {\n    die("Query failed: " . $conn->error);\n}\n\n// Check number of rows\n$numrows = $doResults->num_rows;\necho "Number of rows: " . $numrows . "<br>";\n\n// Fetch and display results\nif ($numrows > 0) {\n    while ($row = $doResults->fetch_assoc()) {\n        // Display each row\n        echo "Field1: " . $row['Field1'] . " - Field2: " . $row['Field2'] . "<br>"; // Replace Field1, Field2 with your actual field names\n    }\n} else {\n    echo "No rows found";\n}\n\n// Free result set\n$doResults->free();\n\n// Close connection\n$conn->close();\n?>