<?php\ninclude ("myconnection.php");\n\n// Delete existing data\nif ($stmt = $mysqli->prepare("DELETE FROM mytable")) {\n    $stmt->execute();\n    $stmt->close();\n    echo "Data deleted, now insert: <br /><br />";\n}\n\n// Load the XML file\n$feed = 'cachy/copy.xml';\n$xml = simplexml_load_file($feed);\n\nif ($xml === false) {\n    die("Couldn't fetch the content<br /><br />");\n}\n\necho "Success! Feed available!<br /><br />";\n\n// Prepared statement for inserting data\nif ($stmt = $mysqli->prepare("INSERT INTO mytable (onsaledate, onsaletime, eventdate, eventtime, buyat_short_deeplink_url, product_name, level1, level2, VenueName, VenueDMAID) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?)")) {\n    foreach($xml->xpath('//product') as $products) {\n        $onsaledate = (string)$products->OnsaleDate;\n        $onsaletime = (string)$products->OnsaleTime;\n        $eventdate = (string)$products->EventDate;\n        $eventtime = (string)$products->EventTime;\n        $buyat_short_deeplink_url = (string)$products->buyat_short_deeplink_url;\n        $product_name = htmlspecialchars((string)$products->product_name, ENT_QUOTES);\n        $level1 = (string)$products->level1;\n        $level2 = (string)$products->level2;\n        $VenueName = (string)$products->VenueName;\n        $VenueDMAID = (string)$products->VenueDMAID;\n\n        $stmt->bind_param('ssssssssss', $onsaledate, $onsaletime, $eventdate, $eventtime, $buyat_short_deeplink_url, $product_name, $level1, $level2, $VenueName, $VenueDMAID);\n        $stmt->execute();\n    }\n    $stmt->close();\n} else {\n    die("Preparation failed: (" . $mysqli->errno . ") " . $mysqli->error);\n}\n\n$mysqli->close();\n\necho "Records inserted successfully!";\n?>