<?php\nfunction isSiteAvailable($url) {\n    $parsedUrl = parse_url($url);\n    $host = $parsedUrl['host'];\n    $port = isset($parsedUrl['port']) ? $parsedUrl['port'] : 80;\n\n    $connection = @fsockopen($host, $port, $errno, $errstr, 10);\n\n    if ($connection) {\n        fclose($connection);\n        return true;\n    } else {\n        return false;\n    }\n}\n\n$twitterUrl = "https://twitter.com";\nif (isSiteAvailable($twitterUrl)) {\n    echo "Twitter is available.";\n} else {\n    echo "Twitter is not available.";\n}\n?>