function getTableNamesForUser($userid) {\n    $tables = [];\n    if ($userid >= 1 && $userid <= 20000) {\n        $tables[] = 'friend_friend_1_20000';\n    } elseif ($userid >= 20001 && $userid <= 40000) {\n        $tables[] = 'friend_friend_20001_40000';\n    }\n    // Add more conditions for other ranges\n    return $tables;\n}\n\n$userid = $_SESSION['auto_id'];\n$tables = getTableNamesForUser($userid);\n$friends = [];\n\nforeach ($tables as $table) {\n    $sql = "SELECT friendid FROM $table WHERE userid = '$userid' AND status = '0'";\n    $result = executeQuery($sql);\n    while ($row = fetchResult($result)) {\n        $friends[] = $row['friendid'];\n    }\n}