// src/Controller/AjaxController.php\n   namespace App\Controller;\n\n   use Symfony\Component\HttpFoundation\Request;\n   use Symfony\Component\HttpFoundation\JsonResponse;\n   use Symfony\Component\Routing\Annotation\Route;\n\n   class AjaxController\n   {\n       /**\n        * @Route("/ajax-request", name="ajax_request", methods={"POST"})\n        */\n       public function handleRequest(Request $request)\n       {\n           $data = json_decode($request->getContent(), true);\n\n           // Validate input\n           if (!isset($data['inputField']) || !is_string($data['inputField'])) {\n               return new JsonResponse(['status' => 'error', 'message' => 'Invalid input'], 400);\n           }\n\n           // Process the request and return a response\n           return new JsonResponse(['status' => 'success', 'data' => $data]);\n       }\n   }