function xmlToArray($xml) {\n    $json = json_encode($xml);\n    $array = json_decode($json,TRUE);\n    return $array;\n}\n\n$xmlString = <<<XML\n<items>\n    <item>1</item>\n    <item>2</item>\n    <item>\n        <item3_1>3.1</item3_1>\n        <item3_2>3.2</item3_2>\n        <isawesome>true</isawesome>\n    </item>\n</items>\nXML;\n\n$xml = simplexml_load_string($xmlString);\n$array = xmlToArray($xml);\n\nprint_r($array);