function keyPathExists($keyPath, $array) {\n    $keys = explode('->', $keyPath);\n    foreach ($keys as $key) {\n        if (!isset($array[$key])) {\n            return false;\n        }\n        $array = $array[$key];\n    }\n    return true;\n}\n\n$array1 = ["foo" => ["bar" => ["baz" => 1]]];\n$array2 = ["foo" => ["bar" => ["baz" => 2]]];\n$keyPath = "foo->bar->baz";\n\nif (keyPathExists($keyPath, $array1) && keyPathExists($keyPath, $array2)) {\n    echo "The key path '$keyPath' exists in both arrays.";\n} else {\n    echo "The key path '$keyPath' does not exist in both arrays.";\n}