// public/index.php\n   use Psr\Http\Message\ServerRequestInterface as Request;\n   use Psr\Http\Message\ResponseInterface as Response;\n   use Slim\Factory\AppFactory;\n\n   require __DIR__ . '/../vendor/autoload.php';\n\n   $app = AppFactory::create();\n\n   $app->post('/ajax-request', function (Request $request, Response $response, $args) {\n       $input = $request->getParsedBody();\n\n       // Validate input\n       if (!isset($input['inputField']) || !is_string($input['inputField'])) {\n           return $response->withJson(['status' => 'error', 'message' => 'Invalid input'], 400);\n       }\n\n       // Process the request and return a response\n       $response->getBody()->write(json_encode(['status' => 'success', 'data' => $input]));\n       return $response->withHeader('Content-Type', 'application/json');\n   });\n\n   $app->run();