function update_roster() {\n    $url         = "http://example.com/";  \n    $userID      = "xx";  \n    $apikey      = "xxx";  \n    $passfields  = "userID=$userID&apikey=$apikey";\n\n    $ch = curl_init();  \n    curl_setopt($ch, CURLOPT_POST, 1);  \n    curl_setopt($ch, CURLOPT_URL, $url);  \n    curl_setopt($ch, CURLOPT_POSTFIELDS, $passfields);  \n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);  // This will return the response as a string\n    $variable_in_question = curl_exec($ch);  \n    curl_close($ch);\n\n    if ($variable_in_question === false) {\n        // Handle the error\n        return "cURL Error: " . curl_error($ch);\n    }\n\n    return $variable_in_question;\n}