<?php\nrequire_once('adLDAP.php');\n\ntry {\n    $adldap = new adLDAP\adLDAP(array(\n        'base_dn' => 'DC=yourdomain,DC=com',\n        'account_suffix' => '@yourdomain.com',\n        'domain_controllers' => array('dc1.yourdomain.com', 'dc2.yourdomain.com'),\n        'ad_username' => 'service_account_username',\n        'ad_password' => 'service_account_password',\n    ));\n} catch (adLDAP\adLDAPException $e) {\n    echo "Connection to AD failed: {$e->getMessage()}";\n    exit();\n}\n\n// Authenticate a user\n$username = 'username';\n$password = 'password';\n\nif ($adldap->authenticate($username, $password)) {\n    echo "User authenticated successfully.";\n} else {\n    echo "Invalid credentials.";\n}\n?>