function isSequentialArray(array $array) {\n    if (array() === $array) return true; // If the array is empty, it's considered sequential\n    return array_keys($array) === range(0, count($array) - 1);\n}\n\n// Examples\n$sequentialArray = [\n    'apple', 'orange', 'tomato', 'carrot'\n];\n\n$assocArray = [\n    'fruit1' => 'apple',\n    'fruit2' => 'orange',\n    'veg1' => 'tomato',\n    'veg2' => 'carrot'\n];\n\nvar_dump(isSequentialArray($sequentialArray)); // true\nvar_dump(isSequentialArray($assocArray)); // false