<?php\n\nfunction inferDataType($value) {\n    if (is_numeric($value)) {\n        if (strpos($value, '.') !== false) {\n            return 'float';\n        } else {\n            return 'int';\n        }\n    } elseif (empty($value)) {\n        return 'varchar(255)'; // Default type for empty values\n    } else {\n        return 'varchar(255)';\n    }\n}\n\n$inputFile = 'file.csv';\n$tableName = 'file_csv';\n\n$fh = fopen($inputFile, 'r');\n$firstLine = fgetcsv($fh);\nfclose($fh);\n\nif ($firstLine === false) {\n    die('Error reading CSV file.');\n}\n\necho 'CREATE TABLE IF NOT EXISTS `'.$tableName.'` ('."<br/>\n";\nforeach ($firstLine as $index => $value) {\n    $typeInfo = inferDataType($value);\n    echo "\t".'`column'.$index.'` '.$typeInfo.' NOT NULL,'."<br/>\n";\n}\necho '  PRIMARY KEY  (`column0`)'."<br/>\n";\necho ') ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=1 ;';\n?>