$xmlString = '<root><element attribute="value">Content</element></root>';\n$dom = new DOMDocument();\n$dom->loadXML($xmlString);\n\n// Accessing elements\n$elements = $dom->getElementsByTagName('element');\nforeach ($elements as $element) {\n    echo $element->nodeValue; // Outputs: Content\n    // Accessing attributes\n    $attribute = $element->getAttribute('attribute');\n    echo $attribute; // Outputs: value\n}