function loadLanguage($lang) {\n       static $cache = [];\n       if (isset($cache[$lang])) {\n           return $cache[$lang];\n       }\n\n       $file = __DIR__ . "/languages/{$lang}.php";\n       if (file_exists($file)) {\n           $cache[$lang] = include($file);\n       } else {\n           $cache[$lang] = include(__DIR__ . "/languages/en.php"); // fallback to default language\n       }\n       return $cache[$lang];\n   }