<?php\nrequire_once('nusoap.php');\n\n// Initialize the client with the WSDL URL\n$client = new nusoap_client('http://example.com/service?wsdl', 'wsdl');\n\n// Check for errors\n$err = $client->getError();\nif ($err) {\n    echo 'Constructor error: ' . $err;\n    exit();\n}\n\n// Define the method name and parameters\n$method = 'YourOperationName';\n$params = array('param1' => 'value1', 'param2' => 'value2');\n\n// Specify the SoapAction\n$soapaction = 'http://example.com/YourOperationName';\n\n// Call the method\n$result = $client->call($method, $params, '', $soapaction);\n\n// Check for errors\nif ($client->fault) {\n    echo 'Fault: ';\n    print_r($result);\n} else {\n    $err = $client->getError();\n    if ($err) {\n        echo 'Error: ' . $err;\n    } else {\n        echo 'Result: ';\n        print_r($result);\n    }\n}\n?>