function loadLanguage($lang) {\n       $file = __DIR__ . "/languages/{$lang}.php";\n       if (file_exists($file)) {\n           return include($file);\n       }\n       return include(__DIR__ . "/languages/en.php"); // fallback to default language\n   }\n\n   $lang = 'es'; // This should be dynamically determined, e.g., from user settings or browser settings\n   $translations = loadLanguage($lang);