<?php\n// Connect to your PostgreSQL database\n$conn = pg_connect("host=your_host dbname=your_db user=your_user password=your_password");\n\n// Your insert query with the RETURNING clause to get the inserted ID\n$query = "INSERT INTO your_table (column1, column2) VALUES ('value1', 'value2') RETURNING id";\n\n// Execute the query\n$result = pg_query($conn, $query);\n\n// Fetch the returned ID\nif ($result) {\n    $inserted_id = pg_fetch_result($result, 0, 'id');\n    echo "Inserted ID: " . $inserted_id;\n} else {\n    echo "Error in insertion: " . pg_last_error($conn);\n}\n\n// Close the database connection\npg_close($conn);\n?>