class LineItem extends Doctrine_Record\n{\n    public function setTableDefinition()\n    {\n        $this->setTableName('line_item');\n        $this->hasColumn('line_total', 'int');\n        $this->hasColumn('order_id', 'int');\n        $this->hasColumn('type', 'integer', null, array('notnull' => true));\n    }\n\n    public function setUp()\n    {\n        $this->hasOne('Order', array('local' => 'order_id', 'foreign' => 'id'));\n        $this->actAs('SingleTableInheritance', array(\n            'type' => 'type',\n            'typeColumn' => 'type',\n            'subclasses' => array(\n                'RentLineItem' => 1,\n                'BuyLineItem' => 2\n            )\n        ));\n    }\n}