<?php\n// Assume $datetime1 and $datetime2 are retrieved from the database\n$datetime1 = '2009-07-26 18:42:21';\n$datetime2 = '2009-07-26 19:42:21';\n\n// Create DateTime objects from the datetime strings\n$date1 = new DateTime($datetime1);\n$date2 = new DateTime($datetime2);\n\n// Calculate the difference between the two DateTime objects\n$interval = $date1->diff($date2);\n\n// Get the total number of seconds elapsed\n$elapsedSeconds = ($interval->days * 24 * 60 * 60) +\n                  ($interval->h * 60 * 60) +\n                  ($interval->i * 60) +\n                  $interval->s;\n\necho "Elapsed seconds: " . $elapsedSeconds;\n?>