<?php\n    function extract_phpinfo_values($html_file) {\n        $config = [];\n        $html = file_get_contents($html_file);\n        preg_match_all('/<tr><td class="e">([^<]+)<\/td><td class="v">([^<]+)<\/td><\/tr>/', $html, $matches, PREG_SET_ORDER);\n\n        foreach ($matches as $match) {\n            $key = trim(strip_tags($match[1]));\n            $value = trim(strip_tags($match[2]));\n            $config[$key] = $value;\n        }\n\n        return $config;\n    }\n\n    function write_php_ini($config, $output_file) {\n        $content = "";\n        foreach ($config as $key => $value) {\n            $content .= "$key = \"$value\"\n";\n        }\n        file_put_contents($output_file, $content);\n    }\n\n    $phpinfo_file = 'phpinfo.html'; // Path to your saved phpinfo HTML file\n    $output_ini_file = 'php.ini';   // Path to output php.ini file\n\n    $config = extract_phpinfo_values($phpinfo_file);\n    write_php_ini($config, $output_ini_file);\n    ?>