<?php\n// Example data from the database\n$var = [\n    (object) ['value' => 'Option1'],\n    (object) ['value' => 'Option2'],\n    (object) ['value' => 'Option3'],\n];\n\n// Custom default value\n$default_value = 'Option2';\n?>\n\n<select>\n    <?php foreach ($var as $row): ?>\n        <option value="<?php echo htmlspecialchars($row->value); ?>" \n            <?php echo ($row->value == $default_value) ? 'selected="selected"' : ''; ?>>\n            <?php echo htmlspecialchars($row->value); ?>\n        </option>\n    <?php endforeach; ?>\n</select>