function insert($param, $table) {\n    // Prepare the SQL statement with placeholders\n    $columns = implode(', ', array_keys($param));\n    $placeholders = str_repeat('?, ', count($param) - 1) . '?';\n    $sqlString = "INSERT INTO $table ($columns) VALUES ($placeholders)";\n    \n    // Prepare the statement\n    if ($statement = $this->conn->prepare($sqlString)) {\n        // Dynamically generate the types string (e.g., 'sss' for three string parameters)\n        $types = str_repeat('s', count($param)); // Assuming all parameters are strings\n        $values = array_values($param);\n\n        // Bind parameters\n        $statement->bind_param($types, ...$values);\n        \n        // Execute the statement\n        if (!$statement->execute()) {\n            die('Error executing statement: ' . $statement->error);\n        }\n\n        // Close the statement\n        $statement->close();\n        return true;\n    } else {\n        // Detailed error handling\n        die("Prepare failed: (" . $this->conn->errno . ") " . $this->conn->error);\n    }\n}