<?php\n// Assuming you have the image data in $imageData\n$imageData = file_get_contents('path/to/your/image.jpg');\n\n// Create an image from the string\n$image = imagecreatefromstring($imageData);\n\n// Get the width and height of the image\n$width = imagesx($image);\n$height = imagesy($image);\n\n// Output the dimensions\necho "Width: " . $width . "px, Height: " . $height . "px";\n\n// Free up memory\nimagedestroy($image);\n?>