<?php\n$nullValue = null;\n$falseValue = false;\n$zeroValue = 0;\n\n// Loose comparison\nvar_dump($nullValue == $falseValue); // true\nvar_dump($nullValue == $zeroValue); // true\nvar_dump($falseValue == $zeroValue); // true\n\n// Strict comparison\nvar_dump($nullValue === $falseValue); // false\nvar_dump($nullValue === $zeroValue); // false\nvar_dump($falseValue === $zeroValue); // false\n\n// Examples of each type\nvar_dump(is_null($nullValue)); // true\nvar_dump(is_bool($falseValue)); // true\nvar_dump(is_int($zeroValue)); // true\n?>