<?php\nrequire_once('phpFlickr/phpFlickr.php');\n\n// Replace with your Flickr API key and secret\n$flickr_api_key = 'your_flickr_api_key';\n$flickr_api_secret = 'your_flickr_api_secret';\n\n// Create a phpFlickr object\n$f = new phpFlickr($flickr_api_key, $flickr_api_secret);\n\n// Search for photos by tag or set\n$photos = $f->photos_search(array(\n    'tags' => 'interior',\n    'per_page' => 20,\n    'format' => 'php_serial'\n));\n\n// Display photos\nif (!empty($photos['photo'])) {\n    echo '<div class="gallery">';\n    foreach ($photos['photo'] as $photo) {\n        $photo_url = $f->buildPhotoURL($photo, 'medium');\n        echo '<div class="photo">';\n        echo '<img src="' . $photo_url . '" alt="' . $photo['title'] . '">';\n        echo '</div>';\n    }\n    echo '</div>';\n} else {\n    echo 'No photos found.';\n}\n?>