<?php\nfunction findClosestMatch($input, $movies) {\n    $shortest = -1;\n    $closest = "";\n\n    foreach ($movies as $movie) {\n        $lev = levenshtein($input, $movie);\n\n        if ($lev == 0) {\n            $closest = $movie;\n            $shortest = 0;\n            break;\n        }\n\n        if ($lev <= $shortest || $shortest < 0) {\n            $closest = $movie;\n            $shortest = $lev;\n        }\n    }\n\n    return $closest;\n}\n\n// Example usage:\n$movies = ["The Matrix", "Inception", "Interstellar", "The Godfather", "Pulp Fiction"];\n$searchTerm = "Incepton"; // Misspelled search term\n$closestMatch = findClosestMatch($searchTerm, $movies);\n\necho "Did you mean: $closestMatch?";\n?>