session_start();\n\nif (!isset($_SESSION['dataentry'])) {\n    $wave_id = intval($_GET['wave_id']); // Use integer casting for security\n    $query_taskinfo = "SELECT participationcode, modulearray, wavenum FROM mng_wave WHERE wave_id=?";\n    \n    // Use prepared statements to prevent SQL injection\n    if ($stmt = $db->prepare($query_taskinfo)) {\n        $stmt->bind_param("i", $wave_id);\n        $stmt->execute();\n        $stmt->bind_result($participationcode, $modulearray, $wavenum);\n        $stmt->fetch();\n        $stmt->close();\n        \n        $dataentry = array(\n            "pcode" => $participationcode,\n            "modules" => $modulearray,\n            "data_id" => 0,\n            "wavenum" => $wavenum,\n            "prequest" => FALSE,\n            "highlight" => array()\n        );\n        \n        $_SESSION['dataentry'] = $dataentry;\n    }\n}