<?php\n$file = 'path/to/your/file.txt'; // Change to the path of your file\n$handle = fopen($file, 'r');\nif ($handle) {\n    // Move to the end of the file\n    fseek($handle, 0, SEEK_END);\n\n    while (true) {\n        // Read and echo new lines\n        while (($line = fgets($handle)) !== false) {\n            echo $line;\n        }\n\n        // Wait for more lines\n        if (feof($handle)) {\n            sleep(1); // Wait for 1 second before checking again\n            clearstatcache(); // Clear the cache to detect file changes\n        }\n    }\n\n    fclose($handle);\n} else {\n    echo "Unable to open the file.";\n}\n?>