<?php\nclass Example {\n    // Define a static property\n    public static $sharedVariable = 0;\n\n    // Method to increment the static property\n    public function increment() {\n        self::$sharedVariable++;\n    }\n\n    // Method to get the current value of the static property\n    public function getSharedVariable() {\n        return self::$sharedVariable;\n    }\n}\n\n$instance1 = new Example();\n$instance2 = new Example();\n\n$instance1->increment();\necho $instance1->getSharedVariable(); // Output: 1\n\n$instance2->increment();\necho $instance2->getSharedVariable(); // Output: 2\n\necho Example::$sharedVariable; // Output: 2\n?>