$tables = ['mod_A', 'mod_B', 'mod_C']; // Add all your module table names to this array\n$section_id = 123; // replace with your actual section_id value\n\n$queryParts = [];\nforeach ($tables as $table) {\n    $queryParts[] = "SELECT * FROM $table WHERE section_id = :section_id";\n}\n$query = implode(" UNION ALL ", $queryParts);\n\n$stmt = $pdo->prepare($query);\n$stmt->bindParam(':section_id', $section_id, PDO::PARAM_INT);\n$stmt->execute();\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n// Process $results as needed