<?php\nclass Object {\n    public $weight;\n\n    public function __construct($weight) {\n        $this->weight = $weight;\n    }\n}\n\n$unsortedObjectArray = [\n    new Object(10),\n    new Object(5),\n    new Object(7),\n];\n\n// Define the comparison function\nusort($unsortedObjectArray, function($a, $b) {\n    return $a->weight <=> $b->weight;\n});\n\nforeach ($unsortedObjectArray as $object) {\n    echo $object->weight . "\n";\n}\n?>