<?php\n// Assuming $conn is your PDO connection object\n\n// Create\n$newData = ['name' => 'John Doe', 'email' => 'john@example.com'];\ncreate($conn, 'users', $newData);\n\n// Retrieve\n$users = retrieve($conn, 'users', ['name' => 'John Doe']);\nprint_r($users);\n\n// Update\n$updateData = ['email' => 'john.doe@example.com'];\nupdate($conn, 'users', $updateData, ['name' => 'John Doe']);\n\n// Delete\ndelete($conn, 'users', ['name' => 'John Doe']);\n?>