$file = 'example.txt';\n\n// Suppress the error that might be thrown by fopen\n$handle = @fopen($file, 'r');\n\nif ($handle === false) {\n    // Handle the error appropriately\n    error_log("Failed to open file: $file");\n    // You can also handle the error by displaying a user-friendly message, retrying, etc.\n    echo "An error occurred while opening the file.";\n} else {\n    // Proceed with file operations\n    // ...\n    fclose($handle);\n}