$file = 'example.txt';\n\nif (file_exists($file) && is_readable($file)) {\n    $handle = fopen($file, 'r');\n    if ($handle === false) {\n        error_log("Failed to open file: $file");\n        echo "An error occurred while opening the file.";\n    } else {\n        // Proceed with file operations\n        // ...\n        fclose($handle);\n    }\n} else {\n    error_log("File does not exist or is not readable: $file");\n    echo "File not found or not readable.";\n}