<?php\nsession_start();\n\nif (!isset($_SESSION['row_index'])) {\n    $_SESSION['row_index'] = 0;\n}\n\nif (isset($_POST['next'])) {\n    $_SESSION['row_index']++;\n} elseif (isset($_POST['previous'])) {\n    $_SESSION['row_index']--;\n}\n\n$row_index = $_SESSION['row_index'];\n\n$sql = "SELECT * FROM your_table LIMIT $row_index, 1";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    $row = $result->fetch_assoc();\n    $value1 = $row['column1'];\n    $value2 = $row['column2'];\n    // Add more columns as needed\n} else {\n    echo "No more rows.";\n    $_SESSION['row_index'] = max(0, $row_index - 1);\n}\n?>\n\n<!DOCTYPE html>\n<html>\n<head>\n    <title>Display Row</title>\n</head>\n<body>\n\n<form method="post">\n    <p>Value 1: <input type="text" name="value1" value="<?php echo isset($value1) ? $value1 : ''; ?>" /></p>\n    <p>Value 2: <input type="text" name="value2" value="<?php echo isset($value2) ? $value2 : ''; ?>" /></p>\n    <!-- Add more textboxes as needed -->\n\n    <button type="submit" name="previous">Previous</button>\n    <button type="submit" name="next">Next</button>\n</form>\n\n</body>\n</html>\n\n<?php\n$conn->close();\n?>