<?php\nfunction update($conn, $table, $data, $conditions) {\n    $updateClauses = [];\n    foreach ($data as $key => $value) {\n        $updateClauses[] = "$key = :$key";\n    }\n    \n    $whereClauses = [];\n    foreach ($conditions as $key => $value) {\n        $whereClauses[] = "$key = :cond_$key";\n    }\n    \n    $sql = "UPDATE $table SET " . implode(", ", $updateClauses) . " WHERE " . implode(" AND ", $whereClauses);\n    $stmt = $conn->prepare($sql);\n    \n    // Merge data and conditions with different keys to prevent conflicts\n    $params = array_merge($data, array_combine(\n        array_map(function($k) { return "cond_$k"; }, array_keys($conditions)),\n        $conditions\n    ));\n    \n    try {\n        return $stmt->execute($params);\n    } catch(PDOException $e) {\n        echo "Error: " . $e->getMessage();\n    }\n}\n?>