function passByValue($param) {\n    // No modification to $param, COW ensures no copying\n}\n\nfunction passByReference(&$param) {\n    // Direct reference, can modify $param\n}\n\n$largeArray = range(1, 1000000);\n\n// Passed by value, but no modification, so no copying happens\npassByValue($largeArray);\n\n// Passed by reference, no need to copy, but references the original variable\npassByReference($largeArray);