<?php\n$a = ["1980-04-14", "2007-06-08", "2008-12-25", "1978-11-03"];\n\nfunction cmp($a, $b) {\n    $currentDate = new DateTime();\n    $currentYear = $currentDate->format('Y');\n\n    $aDate = new DateTime(date('Y-m-d', strtotime($currentYear . substr($a, 4))));\n    $bDate = new DateTime(date('Y-m-d', strtotime($currentYear . substr($b, 4))));\n\n    // If the date has already passed this year, add one year\n    if ($aDate < $currentDate) {\n        $aDate->modify('+1 year');\n    }\n    if ($bDate < $currentDate) {\n        $bDate->modify('+1 year');\n    }\n\n    return $aDate <=> $bDate;\n}\n\nusort($a, 'cmp');\n\nprint_r($a);\n?>