function parseBBCode($text) {\n    $bbcode = array(\n        '/\[b\](.*?)\[\/b\]/is' => '<strong>$1</strong>',\n        '/\[i\](.*?)\[\/i\]/is' => '<em>$1</em>',\n        '/\[u\](.*?)\[\/u\]/is' => '<u>$1</u>',\n        '/\[url\=(.*?)\](.*?)\[\/url\]/is' => '<a href="$1">$2</a>',\n        '/\[img\](.*?)\[\/img\]/is' => '<img src="$1" alt="User submitted image"/>'\n    );\n    foreach ($bbcode as $key => $val) {\n        $text = preg_replace($key, $val, $text);\n    }\n    return htmlspecialchars($text, ENT_QUOTES, 'UTF-8');\n}\n\n// Example usage\n$userInput = "[b]Bold Text[/b] [i]Italic Text[/i] [url=http://example.com]Example[/url]";\n$parsedText = parseBBCode($userInput);\necho $parsedText;