<?php\n// Convert array to JSON\n$array = [\n    // your 2d array data here\n];\n$jsonData = json_encode($array);\nif ($jsonData === false) {\n    echo "Failed to encode data to JSON.";\n    error_log("JSON encode error: " . json_last_error_msg());\n    exit();\n}\n\n// Save or transmit $jsonData as needed\n\n// Later, decode the JSON back to array\n$decodedArray = json_decode($jsonData, true);\nif ($decodedArray === null && json_last_error() !== JSON_ERROR_NONE) {\n    echo "Failed to decode JSON.";\n    error_log("JSON decode error: " . json_last_error_msg());\n    exit();\n}\n\n// Process the decoded array\n?>