// Custom error handler to trigger a warning.\nfunction deprecated_function_warning($function_name) {\n    trigger_error("The function $function_name() is deprecated and should not be used.", E_USER_NOTICE);\n}\n\n// Wrapper for deprecated ibase_connect function\nfunction ibase_connect($args) {\n    deprecated_function_warning('ibase_connect');\n    return call_user_func_array('original_ibase_connect', func_get_args());\n}\n\n// Wrapper for deprecated ibase_query function\nfunction ibase_query($args) {\n    deprecated_function_warning('ibase_query');\n    return call_user_func_array('original_ibase_query', func_get_args());\n}\n\n// Aliasing original functions\nif (!function_exists('original_ibase_connect')) {\n    runkit_function_rename('ibase_connect', 'original_ibase_connect');\n}\nif (!function_exists('original_ibase_query')) {\n    runkit_function_rename('ibase_query', 'original_ibase_query');\n}