<?php\n$searchTerm = "Incepton"; // User input\n\n$query = "SELECT mov_title, MATCH(mov_title) AGAINST(? IN NATURAL LANGUAGE MODE) AS relevance\n          FROM movies\n          WHERE MATCH(mov_title) AGAINST(? IN NATURAL LANGUAGE MODE)\n          ORDER BY relevance DESC\n          LIMIT 10";\n\n$stmt = $pdo->prepare($query);\n$stmt->execute([$searchTerm, $searchTerm]);\n\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\nforeach ($results as $row) {\n    echo $row['mov_title'] . "<br>";\n}\n?>