<?php\n// Read the email from standard input\n$fd = fopen("php://stdin", "r");\n$email = "";\nwhile (!feof($fd)) {\n    $email .= fread($fd, 1024);\n}\nfclose($fd);\n\n// Parse the email headers and body\nlist($headers, $body) = explode("\r\n\r\n", $email, 2);\n\n// Extract the sender's email address\npreg_match('/^From: (.*?)(\r\n|\n|$)/mi', $headers, $matches);\n$sender = trim($matches[1]);\n\n// Process the email body as needed\n$message = trim($body);\n\n// Do something with the sender and message\n// For example, save them to a database or log file\nfile_put_contents('email_log.txt', "Sender: $sender\nMessage: $message\n", FILE_APPEND);\n\n?>