<?php\nfunction isRunningFromCron() {\n    // Check if the TERM environment variable is set to 'dumb'\n    if (getenv('TERM') == 'dumb') {\n        return true;\n    }\n\n    // You can also check other environment variables or use other logic\n    // depending on your specific server configuration and needs.\n\n    return false;\n}\n\nif (isRunningFromCron()) {\n    // Code to execute if run from cron\n    echo "Running from cron job.\n";\n} else {\n    // Code to execute if run manually\n    echo "Running manually from command line.\n";\n    // Add logic to suppress notifications here\n}\n?>