if (isset($this->params['url']['url'])) {\n    $url = filter_var($this->params['url']['url'], FILTER_SANITIZE_URL);\n    $urlParts = explode('/', $url);\n\n    if (!empty($urlParts[0]) && ctype_alnum($urlParts[0])) {\n        $id = $this->Provider->getProviderID($urlParts[0]);\n        $this->providerName = htmlspecialchars($urlParts[0], ENT_QUOTES, 'UTF-8'); // Set the provider name securely\n\n        return $id;\n    } else {\n        // Handle invalid input\n        throw new InvalidArgumentException('Invalid URL parameter.');\n    }\n}