<?php\nfunction is_redirect($url) {\n    $headers = get_headers($url, 1);\n\n    if ($headers !== false && isset($headers[0])) {\n        return preg_match('/^HTTP\/\d\.\d\s+3\d\d\s+/i', $headers[0]);\n    }\n\n    return false;\n}\n\n$url = "http://example.com"; // Replace with the URL you want to check\nif (is_redirect($url)) {\n    echo "The URL redirects.";\n} else {\n    echo "The URL does not redirect.";\n}\n?>