$html = <<<HTML\n   <table>\n       <tr>\n           <th>ID</th>\n           <th>Name</th>\n       </tr>\nHTML;\n\n   // Assume $rows is an array of data fetched from the database\n   foreach ($rows as $row) {\n       $html .= <<<HTML\n       <tr>\n           <td>{$row['id']}</td>\n           <td>{$row['name']}</td>\n       </tr>\nHTML;\n   }\n\n   $html .= '</table>';\n   echo $html;