function format_number($number) {\n    // Format the number to a string with a specified precision\n    $formatted = sprintf("%.10f", $number);\n    // Remove unnecessary trailing zeros\n    $formatted = rtrim($formatted, '0');\n    // Remove the decimal point if it's the last character\n    $formatted = rtrim($formatted, '.');\n    return $formatted;\n}\n\n// Example usage\necho format_number(2);      // Outputs: 2\necho format_number(2.2000); // Outputs: 2.2\necho format_number(100);    // Outputs: 100